mapper.select:
SELECT COUNT(id)
FROM message
WHERE status = 0
AND from_id != 1
AND to_id = #{userId}

    AND conversation_id = #{conversationId}

mapper.update:
UPDATE message SET status = #{status}
WHERE id IN

    #{id}

mapper.select:
SELECT id, from_id, to_id, conversation_id, content, status, create_time
FROM message
WHERE id IN (
SELECT max(id) FROM message
WHERE status != 2
AND from_id = 1
AND to_id = #{userId}
AND conversation_id = #{topic}
)

SELECT count(id) FROM message
WHERE status != 2
AND from_id = 1
AND to_id = #{userId}
AND conversation_id = #{topic}

SELECT count(id) FROM message
WHERE status = 0
AND from_id = 1
AND to_id = #{userId}

    AND conversation_id = #{topic}

SELECT id, from_id, to_id, conversation_id, content, status, create_time
FROM message
WHERE status!=2
AND from_id = 1
AND to_id = #{userId}
AND conversation_id = #{topic}
ORDER by create_time DESC
limit #{offset}, #{limit}
